import React, { useEffect, useState } from "react";
import AutoSuggest from "./AutoSuggest";
import { Person } from "../Models/Person";

function PersonInserter(props) {
    let [people, setPeople] = useState([]);
    let [names, setNames] = useState("");
    
    

    let getPeople = () => {
        let newPeople=[];
        for(let person of props.people){
            if( names.includes(person.name)){
                newPeople.push(person);
            }
        }
        setPeople(newPeople);
        props.updatePeople(newPeople);
    };
    let updateList=(e)=>{
        setNames(e);getPeople()
    }
    return( <div>

        {props.edit==true && <AutoSuggest oldSuggestions={props.oldSuggestions} suggestions={props.people} updateList={(e)=>{updateList(e)}} />}
        <div>
        {people.map((person, i) => (<Person name={person.name} description={person.description} opinions={person.opinions} id={person.id} title={true} key={i} upCounter={() => {}} edit={false} target={(s) => {}} images={person.images} />))}

        </div>
    </div>);
}
export default PersonInserter;
