import React, { useState } from "react";

function PersonMenu(props) {
  let [name, setName] = useState("");
  let [keywords, setKeywords] = useState("");
  let [option,setOption]=useState(false);
  let search = (event) => {
    event.preventDefault();
    props.search({name:name, keywords:keywords});
  };
  let changeAvailability = () => {
    // Toggle the option state
    setOption(!option);
  
    if (option===true) {
      props.newPersonOff();
    } else {
      props.newPersonOn();
    }
    
  }
  return (
    <div>
      <form className="border border-primary mt-sm-2 mb-sm-4" onSubmit={search}>
        <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="name">
            Name:
          </label>
          <div className="col-sm-10">
            <input
              type="text"
              className="form-control"
              id="name"
              name="name"
          
              onChange={(e)=>{let value=e.target.value;
                    setName(value);}}
              value={name} 
            />
          </div>
        </div>
        <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="keywords">
            Keywords:
          </label>
          <div className="col-sm-10">
            <input
              type="text"
              className="form-control"
              id="keyword"
              name="keyword"
            
              onChange={(e)=>{let value=e.target.value;
                    setKeywords(value);}}
              value={keywords} 
            />
          </div>
        </div>
        <input type="submit" className="btn btn-success" value="Search" />
      </form>
      <button onClick={() => { changeAvailability()}}>
        
        { option === true 
               ? 'cancel creation'
               : 'create new person' }
      </button>
    </div>
  );
}

export default PersonMenu;
