import React, { useEffect,useState } from "react";
import { Button } from "semantic-ui-react";

export function Image(props) {
  let [name, setName] = useState(props.name);
  let [description, setDescription] = useState(props.description);
  let [image, setImage] = useState(props.image);
  let [imageUrl, setImageUrl] = useState(null);
  let [oldName, setOldName] = useState(props.name);
  let [oldDescription, setOldDescription] = useState(props.description);
  let [oldImage, setOldImage] = useState(props.image);

  let reset = () => {


setName(oldName);
setDescription(oldDescription);
setImage(oldImage);
setImageUrl(oldImage);
  };


  let removeImage = () => {
    props.delete(props.id);
  };

  function changeImage(e) {
    let selectedFile = e.target.files[0];
    
    const reader = new FileReader();
    reader.onloadend = () => {
      const base64Image = reader.result.split(",")[1];
      setImage(base64Image);
      setImageUrl(base64Image);
    };
    
    reader.onerror = (error) => {
      console.error('Error reading the file:', error);
    };
  
    reader.readAsDataURL(selectedFile);
  }
  
  let filetoBase64 = async (file) => {
    try{
    if (props.image && props.image instanceof File) {
      // If the image is a File object, read it and set the data URL
      const reader = new FileReader();
      reader.onloadend = () => {
        setImageUrl(reader.result);
      };
      
      reader.readAsDataURL(props.image);
    } else {
      // If the image is not a File object, assume it's a URL and use it directly
      setImageUrl(props.image);
    }
    setImage(props.image);}
    catch(e){
      
    }
  }
  useEffect(() => {
    filetoBase64(props.image);
  }, [props.image]);

let submit=(e)=>{
  e.preventDefault();
  try{
  if(image!=""){
   
if(props.newImage===true){
    setImageUrl("");
    setImage("");
    setName("");
    setDescription("");
    setOldDescription("");
    setOldImage("");
    setOldName("");
   }
   props.saveImage({image:image,description:description,name:name}) }}
    catch(e){
    }
  
  }

  
return(<div>
<form onSubmit={(e) => {submit(e)}}>
  <div className="image container">
    <div className="row">
      <img src={`data:image/jpeg;base64,${image}`} alt={name} width="100px" height="100px" />   
    </div>
    <div className="row">
      { props.slider!=false && props.edit  ? < ><label className="col-sm-6 col-form-label" htmlFor="title">title:</label> <input className="col-sm-6" name="title"  type="text" value={name} onChange={(e) => { setName(e.target.value); }} /> </>:<div>{name}</div> }
      {props.slider!=false &&  props.edit ?  <><label className="col-sm-6 col-form-label" htmlFor="description">description:</label> <textarea  className="col-sm-6" name="description" value={description} onChange={(e) => { setDescription(e.target.value); }} /></>:<div>{description}</div> }
      
    </div>
    <div className="row">
      { (props.slider!=false && props.edit)   ? <Button class="btn btn-success col-sm-4 " type="submit">Save Image</Button>:<></>}
      {(props.newImage!==true && image !='')  && <Button class="btn btn-primary col-sm-4 " onClick={() => { reset(); }}>Reset</Button>}
      {(props.newImage!==true && image !='')   && <Button  class="btn btn-danger col-sm-4 " onClick={() => { removeImage(); }}>Delete</Button>}
  
    </div>
  </div>
</form>
</div>);
}

export default Image;
