import React, { useEffect, useState } from "react";
import ReactDOM from 'react-dom/client';
import Image from "../Models/Image";

function ImageSlider(props){
  let [images, setImages] = useState(props.images);
  let [counter,setCounter]=useState(0);
  let [fImage,setFImage]=useState([]);
let [newImage,setNewImage]=useState(false);

  const focusSlides = () => {
   let updatedImage = props.images;
    if(updatedImage==undefined){
      setImages([]);
    }
    else{
        let thisList=props.images;
        let newSlides = [];
        for (let i = 0; i < thisList.length; i++) {
          if (counter >= 0 && counter < thisList.length && Math.abs(i - counter) <= 2) {
            newSlides.push(thisList[i]);
          }
        }
        setFImage(newSlides);
        setImages(props.images);
    }
   
    
  };

  useEffect(() => {
   
      focusSlides();
      if(props.ReadStatus){
          setNewImage(true);
      }
  }, [props.images, counter]);
  

  let deleteImage = async (number) => {
    if(number!=0){
    if(counter===number ){setCounter(number-1)}
    if(number===images.length-1 ){
      let newImages = [...images]; // Create a new copy of the images array
      newImages.pop();
      setImages(newImages);
      props.save(newImages);}
    }
    else{
    let newImages = [...images]; // Create a new copy of the images array
    newImages.splice(number, 1);
    setImages(newImages);
    props.save(newImages);}
  };

  let saveImage = (image, description, name) => {

    
      let newImage = { name: name, description: description, base64: image, id: images[counter].id };
      let newImages = [...images]; // Create a new copy of the images array
      newImages[counter] = newImage;
      setImages(newImages);
      focusSlides();
    };

      
  let step=(value)=>{
      if(value<images.length && value>=0){
        setCounter(value);
        
        focusSlides();
      }
        
    }

    return(
      <div>
          <div className="imageSlider container-fluid pb-3 pt-2">
            <div className="row">
              <button className="col btn btn-secondary text-light" onClick={()=>{step(counter-1)}}>left</button>
              <div  className="col-10" >{(images != undefined && images.length !=0 ) && <Image  slider={true} edit={props.edit} name={images[counter].name || ""} description={images[counter].description || ""} image={images[counter].base64 || ""} id={counter} delete={(e)=>{deleteImage(e)}} saveImage={(e)=>{saveImage(e.image,e.description,e.name)}} newImage={newImage}/>}
              </div>
              
              <button  className="col btn btn-secondary text-light"  onClick={()=>{step(counter+1)}}>right</button>
            </div>
          </div>
          <div className="imageSlider bg-dark container">
            <div className="row">
              {images != undefined && images.length > 1 ? (
                  fImage.map((image, i) => (
                      <a  onClick={() => step(i)}>
                          <img key={i} src={`data:image/jpeg;base64,${image.base64}`} alt={image.name}  className="col-sm-4"/>
                      </a>
                  ))
              ) : <></>}
            </div>
          </div>
      </div>);
        
  }
  export default ImageSlider;
