import React,{ useEffect, useState } from "react";
import axios from 'axios';
import JSOG from "jsog";
import { Person } from "../Models/Person";
import PersonMenu from "../Menus/PersonMenu";

function PersonContainer(props){
    const [persons, setPersons] = useState([]);
    const [name, setName] = useState("");
    const [keywords, setKeywords] = useState("");
    const [PersonQue, setPersonQue] = useState([]);
    const [edit, setEdit] = useState(true);
    const [counter, setCounter] = useState(0);
    const [ofTitle, setOfTitle] = useState(true);
    const [isTrue, setIsTrue] = useState(true);

    let refresh=()=>{
        setPersons([]);
        setName("");
        setKeywords("");
        setPersonQue([]);
        setEdit(true);
        setCounter(0);
        setOfTitle(true);
       
        setIsTrue(true);
        getData();
        props.setTarget(-1);
    }
    const filter = () => {
           
        const newPersons = [];   
      
            for(const person of PersonQue){
                if((name.trim() !== '' && person.name.indexOf(name) >= 0) || (keywords.trim() !== '' && (person.opinions.indexOf(keywords) >= 0 
                || person.description.indexOf(keywords) >= 0 ))){
                    newPersons.push(person);
                }
            }
             setPersons(newPersons);
        
       
    }

    const Filtering = (e) => {
        setOfTitle(true);
        setName(e.name);
        setKeywords(e.keywords);
       
        filter();
    }

    const targetLaunch = (e) => {
        setPersons([]);    
        const newPersons = [];   
        for(const person of PersonQue){
            if(person.id === e){
                newPersons.push(person);
                setOfTitle(false);
            }
        }
        setPersons(newPersons);
       
    }
    //this code is where id does not show.
    const getData = async () => {
        const path = 'http://localhost:8080/person/all';
        let value=[];
        try {
            const res = await axios.get(path);
            const target = JSOG.stringify(res.data);
            const newObject = JSOG.parse(target);
            setPersonQue(newObject["peopleData"]);
            setPersons(newObject["peopleData"]);
            console.log(newObject["peopleData"]);
            value=newObject["peopleData"];
        } catch (error) {
           
        }
        props.peopleUpdate(value);
    }
    let switcher=()=>
    {
        setIsTrue(!isTrue);
        let answer=!isTrue;
        if(answer===false ||edit ===false)
        {refresh();}
        if(props.id!=-1){
            props.setTarget(-1);
        
        }
    }
    let toEdit =()=>
    {
        setEdit(!edit);
    }
    useEffect(() => {
        getData();
    }, [props.id]);

    return (
        <div>
          <PersonMenu
                search={(e) => { Filtering(e); }}
                newPersonOn={() => { switcher(); }}
                newPersonOff={() => {switcher();  }}
            />
            
            <button onClick={() => {toEdit();}}>ToChangeMode</button>
                
            {(edit && isTrue && props.id==-1) && <Person name={""} description={""} 
                    opinions={""} id={-1} title={false} refresh={()=>{refresh();}}
                    upCounter={() => {setCounter(counter + 1)}} edit={edit} toSwitch={true}/>
            }
            {props.id!=-1 && (persons.map((person) => {
                    if(props.id===person.id){return (
                        <Person name={person.name} description={person.description} 
                            opinions={person.opinions} id={person.id}  title={false}  key={person.id}
                             edit={edit} refresh={()=>{refresh();}}
                            target={(s) => {targetLaunch(s)}}   images={person.images}
                        />
                    );}
                }))}
            {(persons.length > 0 && !isTrue && props.id===-1) && (persons.map((person) => {
                return (
                    <Person name={person.name} description={person.description} 
                        opinions={person.opinions} id={person.id}  title={ofTitle}  key={person.id}
                       edit={edit} refresh={()=>{refresh();}}
                    target={(s) => {targetLaunch(s)}}  images={person.images}
                    />
                );
            }))}
        </div>
    );
}

export default PersonContainer;
