import React, { useEffect, useState } from "react";
import Menu from "./Menu";
import PersonContainer from "../Containers/PersonContainer";
import Link from "./Link";

function Main(props) {
  let [category, setCategory] = useState("Person");
  let [id,setId]=useState(-1);
  let [value,setValue]=useState("");
let [count,setCount]=useState(0);
  let handleCategoryChange = (newCategory) => {
      setCategory(newCategory);
      setCount(count++);
  };
  let setPage=(category,value,id)=>{
    handleCategoryChange(category);
    setId(id);
    setValue(value);
  }
let set=()=>{setCategory("");}
useEffect(() => {set(); },[setCount]);
  return (
    <div>
      <Menu CategoryCall={handleCategoryChange} />
      {category === "Person" && <PersonContainer />}
      {category==="Article"&& <div><Link handleClick={setPage(category,value,id)} category={"Person"} value={"Bob Dylan"} id={52}/></div>}
    </div>
  );
}

export default Main;
