import React, { useEffect, useState } from "react";
import Menu from "./Menu";
import PersonContainer from "../Containers/PersonContainer";
import Link from "./Link";

function Main(props) {
  let [category, setCategory] = useState("Person");
  let [id,setId]=useState(-1);
let [count,setCount]=useState(0);
  let handleCategoryChange = (newCategory) => {
      setCategory(newCategory);
      setCount(count++);
  };
  let setPage=(category,id)=>{
    handleCategoryChange(category);
    setId(id);
  }
let set=()=>{setCategory("");}
useEffect(() => {set(); },[setCount]);
  return (
    <div>
      <Menu CategoryCall={handleCategoryChange} />
      {category === "Person" && <PersonContainer targetId={id}/>}
      {category==="Article"&& <div><Link handleClick={setPage(category,id)} category={"Person"} value={"Bob Dylan"} id={52}/></div>}
    </div>
  );
}

export default Main;
