import React, { useEffect, useState } from "react";
import ReactDOM from 'react-dom/client';
import Image from "../Models/Image";

function ImageSlider(props){
  let [images, setImages] = useState(props.images);
  let [counter,setCounter]=useState(0);
  let [fImage,setFImage]=useState([]);
  const focusSlides = async() => {
    let newSlides = [];
    for (let i = 0; i < images.length; i++) {
      if (counter >= 0 && counter < images.length && Math.abs(i - counter) <= 2) {
        newSlides.push(images[i]);
      }
    }
    setFImage(newSlides);
  };
  
  useEffect(() => {
    const fetchData = async () => {
      await updateImages();
      focusSlides();
    };

  let updateImages=async()=>
  {
    setImages(props.images);
    console.log(images);
  
  }

  
    fetchData();
  }, [props.images, counter]);
  

  let deleteImage = async (number) => {
    let newImages = [...images]; // Create a new copy of the images array
    newImages.splice(number, 1);
    setImages(newImages);
    props.save(newImages);
  };

  let saveImage = (image, description, name) => {

    
      let newImage = { name: name, description: description, base64: image, id: images[counter].id };
      let newImages = [...images]; // Create a new copy of the images array
      newImages[counter] = newImage;
      setImages(newImages);
      focusSlides();
    };

      
  let step=(value)=>{
      if(value<images.length && value>=0){
        setCounter(value);
        
        focusSlides();
      }
        
    }

  return(
    <div>
        <div className="imageSlider">
          <button onClick={()=>{step(counter-1)}}>left</button>
          <Image edit={props.edit} name={images[counter].name} description={images[counter].description} image={images[counter].base64} id={counter} delete={()=>{deleteImage()}} save={(e)=>{saveImage(e.image,e.description,e.name)}}/>
          <button onClick={()=>{step(counter+1)}}>right</button>
        </div>
        <div className="imageSlider">
          {images.length > 1 ? (
              fImage.map((image, i) => (
                  <a onClick={() => step(i)}>
                      <img key={i} src={`data:image/jpeg;base64,${image.base64}`} alt={image.name} width="100px" height="100px" />
                  </a>
              ))
          ) : <></>}
        </div>
    </div>);
      
}
export default ImageSlider;
