import React,{ useEffect, useState } from "react";
import axios from 'axios';
import Image from "./Image";
import { Button } from "semantic-ui-react";
import ImageSlider from "../Main/ImageSlider";


export function Person(props) {
  let [name, setName] = useState(props.name || "");
  let [description, setDescription] = useState(props.description || "");
  let [opinions, setOpinions] = useState(props.opinions || "");
  let [images, setImages] = useState(props.images || []);

  useEffect(() => {
   
  }, []);

  let submit = async (event) => {
    event.preventDefault();
    let path = "http://localhost:8080/person";
    let id = props.id;
  
    let imagesData = await Promise.all(images.map(async (image) => {
      if (image.image instanceof File) {
        // Convert File to base64
        const base64Image = await new Promise((resolve, reject) => {
          const reader = new FileReader();
          reader.onloadend = () => resolve(reader.result.split(",")[1]);
          reader.onerror = reject;
          reader.readAsDataURL(image.image);
        });
        
        return {
          name: image.name,
          description: image.description,
          base64: base64Image,
          id:image.id
        };
      }
      
      // If image.image is not a File object, assume it's already a base64 string
      return {
        name: image.name,
        description: image.description,
        base64: image.image,
        id:image.id
      };
    }));
  
    await axios
      .post(path, { id, name, description, opinions, images: imagesData })
      .then((res) => {
        // Handle success
        props.upCounter();
      })
      .catch((error) => {
        // Handle error
        console.log(error.toString());
      });
  };
  

  let deletePerson = async () => {
    if (props.id) {
      await axios.delete(`http://localhost:8080/person/removeById/${props.id}`)
        .then(() => {
          props.upCounter();
        })
        .catch((error) => {
          console.log(error.toString());
        });
    }
  };

  let addImage=async(image,description,name)=>{
    let newImages=images;
    newImages.push({id:-1,name:name,description:description,base64:image});
    setImages(newImages);
  }

    if(props.title){
      return ( <div onClick={()=>{props.target(props.id)}}>
      <div>{name}</div>
        <div>{description}</div>
         <ImageSlider images={props.images} save={(e)=>{setImages(e)}} edit={props.edit}/>
          
      </div>);
    }
    else if(props.edit==false){
      return ( <div id="content">
        <div>
          <div>
            <label htmlFor="name">Name:</label>
            <p id="name">{name}</p>
          </div>
          <div>
            <label htmlFor="description">Description:</label>
            <p id="description">{description}</p>
          </div>
          <div>
            <label htmlFor="opinions">Opinions:</label>
            <p id="opinions">{opinions}</p>
          </div>
        </div>
        <div>
          <ImageSlider images={props.images} save={(e)=>{setImages(e)}} edit={props.edit}/>
        </div>
    </div>);
    
    }
  return (
    <div>
          <ImageSlider images={props.images} save={(e)=>{setImages(e)}} edit={props.edit}/>
          <Image edit={props.edit} name={""} description={""} image={""} newImage={true} saveImage={(e)=>{addImage(e.image,e.description,e.name)}}/>    
      <form  className=" border border-primary mt-sm-2 mb-sm-4" onSubmit={submit} >
     
        <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="name">Name:</label>
          <div className="col-sm-10">
            <input
              type="text"
              id="name"
              className="form-control"
              value={name}
              onChange={(e) => setName(e.target.value)}  
            />
          </div>
        </div>

        <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="description">Description:</label>
          <div className="col-sm-10">
            <textarea
              id="description"
              className="form-control"
              value={description}
              onChange={(e) => setDescription(e.target.value)}  
            />
          </div>
        </div>

        <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="opinions">Opinions:</label>
          <div className="col-sm-10">
            <textarea
              id="opinions"
              className="form-control"
              value={opinions}
              onChange={(e) => setOpinions(e.target.value)} 
            />
          </div>
        </div>

        <div className="form-group row">
          <div className="col-sm-10 offset-sm-2">
            
            {props.id && <Button onClick={deletePerson}>Delete</Button>}
          </div>
        </div>

        <div className="form-group row">
          <div className="col-sm-10 offset-sm-2">
    
           
         
          </div>
        </div>

        <div className="form-group row">
          <div className="col-sm-10 offset-sm-2">
            <input type="submit" className="btn btn-success" value="Save" />
          </div>
        </div>

      </form>
    </div>
  );
}
