import React,{ useEffect, useState } from "react";
import axios from 'axios';
import Image from "./Image";
import { Button } from "semantic-ui-react";


export function Person(props) {
  let [name, setName] = useState(props.name || "");
  let [description, setDescription] = useState(props.description || "");
  let [opinions, setOpinions] = useState(props.opinions || "");
  let [images, setImages] = useState(props.images||[]);

  useEffect(() => {
    // Fetch images associated with this person based on their ID
    if (props.id&&props.id!==-1) {
      axios.get(`http://localhost:8080/images/byPersonId/${props.id}`)
        .then((response) => {
          setImages(response.data);
        })
        .catch((error) => {
          console.log(error.toString());
        });
    }
  }, []);

  let submit = async (event) => {
    event.preventDefault();
    let path = "http://localhost:8080/person";

    if (props.id === -1) {
      // Create a new person
      let perm=false;
      await axios
        .post(path, { name:name, description:description, opinions:opinions,images:images })
        .then((res) => {
          // Handle success
          props.upCounter();
       
        })
        .catch((error) => {
          // Handle error
          console.log(error.toString());
        });

      
    } else {
      // Update an existing person
      path += "/exist";
      axios
        .post(path, { id: props.id, name, description, opinions ,images:images})
        .then((res) => {
          // Handle success
          props.upCounter();
        })
        .catch((error) => {
          // Handle error
          console.log(error.toString());
        });
    }
  };

  let deletePerson = async () => {
    if (props.id) {
      await axios.delete(`http://localhost:8080/person/removeById/${props.id}`)
        .then(() => {
          props.upCounter();
        })
        .catch((error) => {
          console.log(error.toString());
        });
    }
  };


  let deleteImage = async (imageId) => {
    let imagesCopy
    for(let i=0;i<images.length;i++){
      if(images[i].id===imageId&&images[i].id!==-1){
        let newImages = images.filter(image => image.id !== imageId);
        setImages(newImages);
        break;

      }
       else if(images[i].id===imageId&&images[i].id===-1){
          const newArray = [...images];
          newArray.pop();
          setImages(newArray);
      }
    }
   

  };

  let saveImage = (image) => {
    const imageFormatRegex = /\.(jpg|jpeg|png|gif)$/i;
    
    // Check if the image field exists and has a valid image file extension
    if (image.image && imageFormatRegex.test(image.image)) {
      if (image.id === -1) {
        // Create a new image and update the images array
        setImages([...images, image]);
      } else {
        // Update an existing image in the images array
        setImages(images.map((existingImage) => (existingImage.id === image.id ? image : existingImage)));
      }
    } else {
      console.log("Invalid image format or missing image field.");
      // Handle the case where the image is not valid
    }
  };
  
  
    if(props.title){
      return ( <div onClick={()=>{props.target(name)}}>
      <div>{name}</div>
        <div>{description}</div>
        {images.length>0 && <Image className="image-container float-right mt-5 ml-2 border border-dark"
            key={images[0].id}
            name={images[0].name}
            description={images[0].description}
            image={images[0].image}
            id={images[0].id}
            edit={false}
            delete={() => deleteImage(images[0].id)}
          />}
          
      </div>);
    }
    else if(props.edit==false){
      return ( <div id="content">
        <div>
          <div>
            <label htmlFor="name">Name:</label>
            <p id="name">{name}</p>
          </div>
          <div>
            <label htmlFor="description">Description:</label>
            <p id="description">{description}</p>
          </div>
          <div>
            <label htmlFor="opinions">Opinions:</label>
            <p id="opinions">{opinions}</p>
          </div>
        </div>
        <div>
          {images.length>0 && images.map((image) => (
            <Image className="image-container float-right mt-5 ml-2 border border-dark"
              key={image.id}
              name={image.name}
              description={image.description}
              image={image.image}
              id={image.id}
              edit={props.edit}
              delete={() => deleteImage(image.id)}
            />
          ))}
        </div>
    </div>);
    
    }
  return (
    <div>
          <Image
                key={"new image"}
                name={""}
                className="image-container float-right mt-5 ml-2 border border-dark"
                description={""}
                image={""}
                id={-1}
                edit={props.edit}
                adder={true}
                createNew={(e)=>{saveImage(e)}}
              />
               {images.length>0 && images.map((image) => (
              <Image
                key={image.id}
                name={image.name}
                className="image-container float-right mt-5 ml-2 border border-dark"
                description={image.description}
                image={image.image}
                id={image.id}
                edit={props.edit}
                delete={() => deleteImage(image.id)}
                adder={false}
                createNew={(e)=>{saveImage(e)}}
             
              />
            ))}
      <form  className=" border border-primary mt-sm-2 mb-sm-4" onSubmit={submit} >
     
        <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="name">Name:</label>
          <div className="col-sm-10">
            <input
              type="text"
              id="name"
              className="form-control"
              value={name}
              onChange={(e) => setName(e.target.value)}  
            />
          </div>
        </div>

        <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="description">Description:</label>
          <div className="col-sm-10">
            <input
              type="text"
              id="description"
              className="form-control"
              value={description}
              onChange={(e) => setDescription(e.target.value)}  
            />
          </div>
        </div>

        <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="opinions">Opinions:</label>
          <div className="col-sm-10">
            <input
              type="text"
              id="opinions"
              className="form-control"
              value={opinions}
              onChange={(e) => setOpinions(e.target.value)} 
            />
          </div>
        </div>

        <div className="form-group row">
          <div className="col-sm-10 offset-sm-2">
            
            {props.id && <Button onClick={deletePerson}>Delete</Button>}
          </div>
        </div>

        <div className="form-group row">
          <div className="col-sm-10 offset-sm-2">
    
           
         
          </div>
        </div>

        <div className="form-group row">
          <div className="col-sm-10 offset-sm-2">
            <input type="submit" className="btn btn-success" value="Save" />
          </div>
        </div>

      </form>
    </div>
  );
}
