import React, { useEffect,useState } from "react";
import { Button } from "semantic-ui-react";

export function Image(props) {
  let [name, setName] = useState(props.name);
  let [description, setDescription] = useState(props.description);
  let [image, setImage] = useState(props.image);
  let [file, setFile] = useState(null);
  let [imageUrl, setImageUrl] = useState(null);
  let [oldName, setOldName] = useState(props.name);
  let [oldDescription, setOldDescription] = useState(props.description);
  let [oldImage, setOldImage] = useState(props.image);

  let reset = () => {

    setFile(null);
    props.createNew({name:oldName, description:oldDescription, image:oldImage,id:props.id});
  };

  let update = (type,value) => {
    if(type==="name"){
      props.createNew({name:value,description: description,image: image,id:props.id});
    }
    else{
      props.createNew({name:name,description: value, image:image,id:props.id});
    }
    
  };

  let removeImage = () => {
    props.delete(props.id);
  };

  let changeImage = (e) => {
    let selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(URL.createObjectURL(selectedFile));
      setImage(selectedFile);
    }
    if (props.id !== -1) {
      props.createNew({ name: name, description: description, image: image, id: props.id });
    }
  
    // Reset the file input value to prevent storing the file
    e.target.value = ""; // This will clear the file input
  };
  useEffect(() => {
    if (props.image && props.image instanceof File) {
      // If the image is a File object, read it and set the data URL
      const reader = new FileReader();
      reader.onloadend = () => {
        setImageUrl(reader.result);
      };
      reader.readAsDataURL(props.image);
    } else {
      // If the image is not a File object, assume it's a URL and use it directly
      setImageUrl(props.image);
    }
  }, [props.image]);

  if (props.adder) {
    return (
      <div>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)} 
        />
       
       
        {file ? <img src={file} alt="Preview" /> : <img src={image} alt="Can't load Image" />}
        <input type="file" accept="image/*" onChange={changeImage} style={{ margin: "10px 0" }} required id="fileInput" />
        
        
        <label htmlFor="description">Description:</label>
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          id="description"
        />
        <Button onClick={() => {
          setFile(null);
          setName("");
          setDescription("");
          setImage(props.image);
          if (props.image && props.image instanceof File) {props.createNew({ name: name, description: description, image: image, id: props.id });}
          // Reset the file input value to prevent storing the file
          document.getElementById("fileInput").value = ""; // Assuming your file input has an id "fileInput"
        }}>Save Image</Button>
        </div>
    );
  }

  if (props.edit) {
    return (
      <div>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => {
            update("name", e.target.value);
          }}
        />
         {imageUrl && <img src={imageUrl} alt="Preview" />}
        <input
          type="file"
          accept="image/*"
          onChange={(e) => {
            changeImage(e);
             // Assuming you want to update the image state with the selected file
          }}
          style={{ margin: "10px 0" }}
        />
<label htmlFor="description">Description:</label>
        <input
          type="text"
          value={description}
          onChange={(e) => {
            update("description", e.target.value);
          }}
          id="description"
        />
        <div>Remember when changing back to the original image, save again.</div>
        <div>
          <Button onClick={removeImage}>Delete</Button>
          <Button onClick={reset}>Reset</Button>
        </div>
      </div>
    );
    
  } else {
    return (
      <div>
        <div>{name}</div>
        <img src={image || file} alt="Problem loading image" />
        <div>{description}</div>
      </div>
    );
  }
}

export default Image;
