import React, { useEffect, useState } from "react";
import ReactDOM from 'react-dom/client';
import Image from "../Models/Image";

function ImageSlider(props){
    let [images,setImages]=useState(props.images);
    let [counter,setCounter]=useState(0);
    useEffect(() => {
      
    }, []);
    let deleteImage = async (number) => {
     
        let newImages=images;
        newImages.splice(number,1);
        
        setImages(newImages);
        props.save(newImages);
      };
    
      let saveImage = (image,description,name) => {
        let newImage={name:name,description:description,base64:image,id:images[counter].id};
        let newImages=images;
        newImages[counter]=newImage;
        setImages(newImages);}
      
      let step=(value)=>{
          let length=images.length;
          let target=counter+value;
          if(target>=0 && target<length){
          setCounter(()=>{let target=counter+value; console.log(target); return target;});
          }
        }

return(<div>
    <div className="imageSlider">
    <button onClick={()=>{step(-1)}}>left</button>
    <Image edit={props.edit} name={images[counter].name} description={images[counter].description} image={images[counter].base64} id={counter} delete={()=>{deleteImage()}} save={(e)=>{saveImage(e.image,e.description,e.name)}}/>
    <button onClick={()=>{step(1)}}>right</button>
    </div>
    <div className="imageSlider">
    {images.length>1 && images.map((image,i)=>{
        return(<a onclick={()=>step(i)}><img src={image.base64} alt={image.name} width="100px" height="100px"/></a>);
    })}
    </div>
</div>);
      
}
export default ImageSlider;
