import React, { useEffect, useState } from "react";
import Menu from "./Menu";
import PersonContainer from "../Containers/PersonContainer";
import Link from "./Link";
import AnalysisContainer from "../Containers/AnalysisContainer";

function Main(props) {
  let [category, setCategory] = useState("Person");
  let [id,setId]=useState(-1);
  let [count,setCount]=useState(0);
let [people,setPeople]=useState([]);
  let handleCategoryChange = (e) => {
   
    if(e.category===undefined){
      setCategory(e);
    }
    else{
      setCategory(e.category);
    }
     
      setCount(count+1);
      
      if(e.id!==undefined){
        setId(e.id);
      }
      else{
        setId(-1);
      }
      
  };
  let setPage=(e)=>{
    handleCategoryChange({category:e.category,id:e.id});
    
  }

let peopleUpdate=(e)=>{
  setPeople(e);
}
  useEffect(() => {
  }, []);
  return (
    <div>
      <Menu CategoryCall={handleCategoryChange} />
      {category === "Person" && <PersonContainer id={id} peopleUpdate={(e)=>peopleUpdate(e)}/>}
      {category==="Analysis"&& <div> <AnalysisContainer id={id} count={props.count} people={people} setTarget={(e)=>setId(e)} />
</div>}
    </div>
  );
}

export default Main;
