import React,{ useEffect, useState } from "react";
import axios from 'axios';
import JSOG from "jsog";
import { Analysis } from "../Models/Analysis";
import AnalysisMenu from "../Menus/AnalysisMenu";

function AnalysisContainer(props){
    let [analysis, setAnalysis] = useState([]);
    let [title, setTitle] = useState("");
    let [keywords, setKeywords] = useState("");
    let [analysisQue, setAnalysisQue] = useState([]);
    let [edit, setEdit] = useState(false);
    let [counter, setCounter] = useState(0);
    let [isTrue, setIsTrue] = useState(true);
    let [dateFrom,setDateFrom]=useState("");
    let [dateTo, setDateTo]=useState("");

    let filter = () => {
           
        let newAnalysis = [];   
        if(keywords.trim() !== '' || title.trim() !== ''){
            for(let analysis of analysisQue){
                if(analysis.title.indexOf(title) >= 0 || analysis.keyTakeaways.indexOf(keywords) >= 0 
                || analysis.content.indexOf(keywords) >= 0 ||(dateFrom!="" && analysis.dateFrom >= dateFrom) 
                ||(dateTo!="" && analysis.dateTo <= dateTo)){
                    newAnalysis.push(analysis);
                }
            }
        }
        setAnalysisQue(newAnalysis);
        props.setTarget(-1);
    }

    let Filtering = (e) => {
        
        setTitle(e.title);
        setKeywords(e.keywords);
        setDateTo(e.dateTo);
        setDateFrom(e.dateFrom);
        filter();
    }

    let targetLaunch = (e) => {
        
        let newAnalysis = [];   
        for(let analysis of analysisQue){
            if(analysis.id === e){
                newAnalysis.push(analysis);
                
            }
        }
        setAnalysisQue(newAnalysis);
        props.setTarget(e);
    }

    let getData = async () => {
        let path = 'http://localhost:8080/analysis/all';
        try {
            let res = await axios.get(path);
            let target = JSOG.stringify(res.data);
            let newObject = JSOG.parse(target);
            setAnalysisQue(newObject["analysisData"]);
            setAnalysis(newObject["analysisData"]);
            console.log(newObject["analysisData"]);
        } catch (error) {
            console.log(error);
        }
    }

    let npon = () => {
        setIsTrue(true);
   
    }

    let npoff = () => {
        setIsTrue(false);
      
    }

    useEffect(() => {
        getData();
    }, []);

    return (
        <div>
            <AnalysisMenu
                search={(e) => { Filtering(e); }}
                newAnalysisOn={() => { npon(); }}
                newAnalysisOff={() => { npoff(); }}
            />
            
            <button onClick={() => {setEdit(!edit); }}>ToChangeMode</button>
                
            {(edit && props.id==-1 && isTrue) && <Analysis content={""}  people={props.people}
                    keyTakeaways={""} id={-1} toList={false}  title={""} dateOfAnalysis={""} oldSuggestions={[]}
                  upDateList={()=>{setCounter(counter + 1);getData()}} />
            }
            {analysisQue.length>0 && analysisQue.map((analysis) => {
                analysisQue.length > 1 
                    ? 
                        (<Analysis
                        title={analysis.title}
                        content={analysis.content}
                        keyTakeaways={analysis.keyTakeaways}
                        id={analysis.id}
                        toList={true}
                        key={analysis.id}
                        upDateList={()=>{setCounter(counter + 1);getData()}}
                        edit={edit}
                        dateOfAnalysis={analysis.dateOfAnalysis}
                        target={(s) => {
                            targetLaunch(s);
                        }}
                        images={analysis.images}
                        people={props.people}
                        oldSuggestions={analysis.people}
                        />)
                    
                    : 
                        (<Analysis
                        title={analysis.title}
                        content={analysis.content}
                        keyTakeaways={analysis.keyTakeaways}
                        id={analysis.id}
                        toList={false}
                        key={analysis.id}
                        upDateList={()=>{setCounter(counter + 1);getData()}}
                        edit={edit}
                        dateOfAnalysis={analysis.dateOfAnalysis}
                        target={(s) => {
                            targetLaunch(s);
                        }}
                        images={analysis.images}
                        people={props.people}
                        oldSuggestions={analysis.people}
                        />)
                    
                }
                )}
        </div>
    );
}

export default AnalysisContainer;
