import React, { useEffect, useState } from "react";

function Menu(props) {
  const [category, setCategory] = useState("Person");

  const changeCategory = (target) => {
    setCategory(target);
    props.CategoryCall(target); // Use 'target' directly
  };

  const handleLinkClick = (e, target) => {
    e.preventDefault(); // Prevent default anchor tag behavior
    changeCategory(target);
  };

  return (
    <div className="mh-30 mw-100 container mb-3 mt-2 text-light bg-dark">
      <div className="row">
        <div className="col h1">Ashok Dutta</div>
        
      
      
      
      <div className="col bg-light">

      <button
        className="link-opacity-100"
        onClick={(e) => handleLinkClick(e, "Person")}
      >
        Person
      </button>
      <button
        className="link-opacity-100"
        onClick={(e) => handleLinkClick(e, "Analysis")}
      >
        Analysis
      </button>
      <button
        className="link-opacity-100"
        onClick={(e) => handleLinkClick(e, "Article")}
      >
        Article
      </button>
      <button
        className="link-opacity-100"
        onClick={(e) => handleLinkClick(e, "Award")}
      >
        Award
      </button>
      <button
        className="link-opacity-100"
        onClick={(e) => handleLinkClick(e, "Conferences")}
      >
        Conferences
      </button>
      <button
        className="link-opacity-100"
        onClick={(e) => handleLinkClick(e, "Podcast")}
      >
        Podcast
      </button>
      <button
        className="link-opacity-100"
        onClick={(e) => handleLinkClick(e, "Trip")}
      >
        Trip
      </button>
    </div>
    </div>
    <div className="row"><div className="col"><u></u></div></div>
    </div>
  );
}

export default Menu;
