import React, { useEffect, useState } from "react";
import ReactDOM from 'react-dom/client';
import Image from "../Models/Image";

function ImageSlider(props){
    let [images,setImages]=useState(props.images);
    let [counter,setCounter]=useState(0);
    useEffect(() => {
      
    }, []);
    let deleteImage = async (number) => {
     
        let newImages=images;
        newImages.splice(number,1);
        
        setImages(newImages);
        props.save(newImages);
      };
    
      let saveImage = (counter,image,isNew) => {
          if(image.base64!==""){
            if(isNew){
            setImages([...images, image]);
          }
          else{
            let newImages=images;
            newImages[counter]=image;
            setImages(newImages);

          }
          props.save(images);}
      };
      let step = (value) => {
        let length = images.length;
        let target = counter + value;
        if (target >= 0 && target < length) {
          setCounter((prevCounter) => {
            let newCounter = prevCounter + value;
            console.log(newCounter); // This will log the updated value of counter
            return newCounter;
          });
        }
      }
      

return(<div>
{images.length>0 && 
<div> 
  <div>hello
    {1===1 && <Image
               
                name={images[counter].name}
                className="image-container float-right mt-5 ml-2 border border-dark"
                description={images[counter].description}
                image={images[counter].base64}
                id={images[counter].id}
                edit={props.edit}
                delete={() => deleteImage(counter)}
                adder={false}
                createNew={(e)=>{saveImage(counter,e,false)}}
                slide={false}
                key={1}
              />}
    </div>
    <div>
        {counter-2>=0 &&  <Image
            
                name={images[counter-2].name}
                className="image-container float-right mt-5 ml-2 border border-dark"
                description={images[counter-2].description}
                image={images[counter-2].base64}
                id={images[counter-2].id}
                edit={props.edit}
                delete={() => deleteImage(counter-2)}
                adder={false}
                createNew={(e)=>{saveImage(counter,e,false)}}
                move={()=>{step(-2)}}  
                slide={true}
              />}
        {counter-1>=0 &&  <div>this is -1.<Image
              
                name={images[counter-1].name}
                className="image-container float-right mt-5 ml-2 border border-dark"
                description={images[counter-1].description}
                image={images[counter-1].base64}
                id={images[counter-1].id}
                edit={props.edit}
                delete={() => deleteImage(counter-1)}
                adder={false}
                createNew={(e)=>{saveImage(counter,e,false)}}
                move={()=>{step(-1)}}  
                slide={true}
              /></div>}
        {counter+1<images.length &&<div>this is +1 <Image
   
                name={images[counter+1].name}
                className="image-container float-right mt-5 ml-2 border border-dark"
                description={images[counter+1].description}
                image={images[counter+1].base64}
                id={images[counter+1].id}
                edit={props.edit}
                delete={() => deleteImage(counter+1)}
                adder={false}
                createNew={(e)=>{saveImage(counter,e,false)}}
                move={()=>{step(1)}}  
                slide={true}
              /></div> }
        {counter+2<images.length &&  <Image
          
                name={images[counter+2].name}
                className="image-container float-right mt-5 ml-2 border border-dark"
                description={images[counter+2].description}
                image={images[counter+2].base64}
                id={images[counter+2].id}
                edit={props.edit}
                delete={() => deleteImage(counter+2)}
                adder={false}
                createNew={(e)=>{saveImage(counter,e,false)}}
                move={()=>{step(2)}}
                slide={true}
              />}
              <button  type="button" onClick={()=>{step(-1)}}>Previous</button>
              <button  type="button" onClick={()=>{step(1)}}>Next</button>
              </div>
              </div>}
   
              {props.edit===true&& <Image              
                name={""}
                className="image-container float-right mt-5 ml-2 border border-dark"
                description={""}
                image={""}
                id={-1}
                edit={props.edit}
                delete={() => deleteImage(images[counter+2].id)}
                adder={true}
                createNew={(e)=>{saveImage(counter,e,true)}}
                slide={false}
              />}
</div>);
      
}
export default ImageSlider;
