import React, { useEffect,useState } from "react";

function AnalysisMenu(props) {
  let [name, setName] = useState("");
  let [keywords, setKeywords] = useState("");
  let [option,setOption]=useState(props.isOn);
  let [dateFrom, setDateFrom] = useState("");
  let [dateTo, setDateTo] = useState("");

  useEffect(() => {
    
}, [props.isOn]);

  let search = (event) => {
    event.preventDefault();
try{
    let date1 = new Date(dateFrom).getTime();
  let date2 = new Date(dateTo).getTime();
    if((date1<date2)||dateTo == "" ||dateFrom == ""){props.search({name:name, keywords:keywords ,dateFrom:dateFrom, dateTo:dateTo});}
    else{alert("dates are not chronologically ordered")} } 
    catch{}

  };
  let changeAvailability = () => {
    
  
    if (option===true) {
      props.newAnalysisOff();
    } else {
      props.newAnalysisOn();
    }
    
  }
  return (
    <div>
      <form className="container" onSubmit={search}>
        <div className="row">
          <div className="col-sm-12"> {/* Full-width column */}
            <div className="form-group row">
              <label className="col-sm-2 col-form-label" htmlFor="name">
                Name:
              </label>
              <div className="col-sm-10">
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  name="name"
                  onChange={(e) => {
                    let value = e.target.value;
                    setName(value);
                  }}
                  value={name}
                />
              </div>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-sm-12"> {/* Full-width column */}
            <div className="form-group row">
              <label className="col-sm-2 col-form-label" htmlFor="keywords">
                Keywords:
              </label>
              <div className="col-sm-10">
                <textarea
                  className="form-control"
                  id="keywords"
                  name="keywords"
                  onChange={(e) => {
                    let value = e.target.value;
                    setKeywords(value);
                  }}
                  value={keywords}
                />
              </div>
            </div>
          </div>
        </div>

        {/* ... (other form groups) */}

        <input type="submit" className="btn btn-success" value="Search" />
        <button
          type="button"
          className="btn btn-primary"
          onClick={() => {
            changeAvailability();
          }}
        >
          New Analysis
        </button>
      </form>
    </div>
  );
}

export default AnalysisMenu;
