import React, { useEffect, useState } from "react";
import Menu from "./Menu";
import PersonContainer from "../Containers/PersonContainer";
import Link from "./Link";

function Main(props) {
  let [category, setCategory] = useState("Person");
  let [id,setId]=useState(-1);
let [count,setCount]=useState(0);
  let handleCategoryChange = (e) => {
      setCategory(e.category);
      setCount(count+1);
      
      if(e.id!=undefined){
        setId(e.id);
      }
      else{
        setId(-1);
      }
  };
  let setPage=(e)=>{
    handleCategoryChange({category:e.category,id:e.id});
    
  }
  

  useEffect(() => {
  }, []);
  return (
    <div>
      <Menu CategoryCall={handleCategoryChange} />
      {category === "Person" && <PersonContainer targetId={id}/>}
      {category==="Article"&& <div><Link handleClick={(e) => setPage(e)} category={"Person"} value={"Bob Dylan"} id={52} />
</div>}
    </div>
  );
}

export default Main;
