import React, { useState } from "react";
import { Button } from "semantic-ui-react";


export function Image(props) {
  let [name, setName] = useState(props.name);
  let [description, setDescription] = useState(props.description);
  let [image, setImage] = useState(props.image);
  let [file, setFile] = useState(null);


  let createNew=()=>{
    props.createNew(name,description,image);
  }
  let removeImage = () => {
    props.delete(props.id);
  };

  let changeImage = (e) => {
    let selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(URL.createObjectURL(selectedFile));
      setImage(image || props.image);
    }
  };

 
if(props.adder){

  return (
    <div>
      <label htmlFor="name">Name:</label>
      <input
        type="text"
        id="name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      {file ? (
        <img src={file} alt="Preview" />
      ) : (
        <img src={image} alt="Can't load Image" />
      )}
      <input
        type="file"
        accept="image/*"
        onChange={changeImage}
        style={{ margin: "10px 0" }}
      />
      <label htmlFor="description">Description:</label>
      <input
        type="text"
        value={"description"}
        onChange={(e) => setDescription(e.target.value)}
        id="description"
      />
      <div>
      </div>
      <Button onClick={createNew}>save Image</Button>
    </div>);}
  if (props.edit) {
    return (
      <div>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        {file ? (
          <img src={file} alt="Preview" />
        ) : (
          <img src={image} alt="Can't load Image" />
        )}
        <input
          type="file"
          accept="image/*"
          onChange={changeImage}
          style={{ margin: "10px 0" }}
        />
        <label htmlFor="description">Description:</label>
        <input
          type="text"
          value={description}
          onChange={(e) => {setDescription(e.target.value);}}
          id="description"
        />
        <div>Remember when changing back to the original image, save again.</div>
        <div>
        
          <Button onClick={removeImage}>Delete</Button>
        </div>
      </div>
    );
  } else {
    return (
      <div  >
        <div>{name}</div>
        <img src={image || file} alt="Problem loading image" />
        <div>{description}</div>
      </div>
    );
  }
}
export default Image;
