import React,{ useEffect, useState } from "react";
import axios from 'axios';
import JSOG from "jsog";
import { Analysis } from "../Models/Analysis";
import AnalysisMenu from "../Menus/AnalysisMenu";

function AnalysisContainer(props){
    let [analysis, setAnalysis] = useState([]);
    let [analysisQue, setAnalysisQue] = useState([]);
    let [edit, setEdit] = useState(false);
    let [counter, setCounter] = useState(0);
    let [isTrue, setIsTrue] = useState(false);
 

   
    const Filtering = (e) => {
        const { name, keywords, dateTo, dateFrom } = e;
    
        const filteredList = analysis.filter(analysisT => {
            const dateTarget = analysisT.dateOfAnalysis ? new Date(analysisT.dateOfAnalysis) : null;
            const hasValidTitle = !name || analysisT.title.skeyTakeaways.includes(keywords) && keywords.trim().length > 0;
            const hasValidKeywords = !keywords || analysisT.content.includes(keywords) && keywords.trim().length > 0;
            const isDateInRange = (!dateFrom || !dateTarget || new Date(dateTarget) > new Date(dateFrom)) &&
                                  (!dateTo || !dateTarget || new Date(dateTarget) < new Date(dateTo));
    
            return hasValidTitle && hasValidKeywords && isDateInRange;
        });
    
        setAnalysisQue(filteredList);
        props.setTarget(-1);
    };
    

    let targetLaunch = (e) => {
            
        let newAnalysis = [];   
        for(let analysis of analysisQue){
            if(analysis.id === e){
                newAnalysis.push(analysis);
                
            }
        }
        setAnalysisQue(newAnalysis);
        props.setTarget(e);
    }

    let getData = async () => {
        let path = 'http://localhost:8080/analysis/all';
        try {
            let res = await axios.get(path);
            let target = JSOG.stringify(res.data);
            let newObject = JSOG.parse(target);
            setAnalysisQue(newObject["analysisData"]);
            setAnalysis(newObject["analysisData"]);
            console.log(newObject["analysisData"]);
        } catch (error) {
            console.log(error);
        }
    }

    let npon = () => {
        setIsTrue(true);
        setEdit(true);
    }

    let npoff = () => {
        setIsTrue(false);
      setEdit(false);
    }

    useEffect(() => {
        getData();
    }, []);

    return (
        <div>
            <AnalysisMenu
                search={(e) => { Filtering(e); }}
                newAnalysisOn={() => { npon(); }}
                newAnalysisOff={() => { npoff(); }}
            />
            
            <button onClick={() => {setEdit(!edit); }}>ToChangeMode</button>
                
            {(edit && props.id==-1 && isTrue) && <Analysis content={""}  people={props.people}
                    keyTakeaways={""} id={-1} toList={false}  title={""} dateOfAnalysis={""} oldSuggestions={[]}
                  upDateList={()=>{setCounter(counter + 1);getData()}} edit={edit}/>
            }
            {(isTrue===false && analysisQue.length > 1) && analysisQue.map((analysis) => {return ( <Analysis
                        title={analysis.title}
                        content={analysis.content}
                        keyTakeaways={analysis.keyTakeaways}
                        id={analysis.id}
                        toList={true}
                        key={analysis.id}
                        upDateList={()=>{setCounter(counter + 1);getData()}}
                        edit={edit}
                        dateOfAnalysis={analysis.dateOfAnalysis}
                        target={(s) => {
                            targetLaunch(s);
                        }}
                        images={analysis.images}
                        people={props.people}
                        oldSuggestions={analysis.people}
                        />)})}
            {(isTrue===false && analysisQue.length === 1) && analysisQue.map((analysis) => {return (
                        <Analysis
                        title={analysis.title}
                        content={analysis.content}
                        keyTakeaways={analysis.keyTakeaways}
                        id={analysis.id}
                        toList={false}
                        key={analysis.id}
                        upDateList={()=>{setCounter(counter + 1);getData()}}
                        edit={edit}
                        dateOfAnalysis={analysis.dateOfAnalysis}
                        target={(s) => {
                            targetLaunch(s);
                        }}
                        images={analysis.images}
                        people={props.people}
                        oldSuggestions={analysis.people}
                        />)})}
            
                       
                    
                     
                    
               
        </div>
    );
}

export default AnalysisContainer;
