import React, { useEffect,useState } from "react";

function AnalysisMenu(props) {
  let [name, setName] = useState("");
  let [keywords, setKeywords] = useState("");
  let [option,setOption]=useState(props.isOn);
  let [dateFrom, setDateFrom] = useState("");
  let [dateTo, setDateTo] = useState("");

  useEffect(() => {
    
}, [props.isOn]);

  let search = (event) => {
    event.preventDefault();
    if((dateFrom<dateTo)||dateTo == "" ||dateFrom == ""){props.search({name:name, keywords:keywords ,dateFrom:dateFrom, dateTo:dateTo});}
    else{alert("dates are not chronologicly ordered")}

  };
  let changeAvailability = () => {
    
  
    if (option===true) {
      props.newAnalysisOff();
    } else {
      props.newAnalysisOn();
    }
    
  }
  return (
    <div>
      <form className="border border-primary mt-sm-2 mb-sm-4" onSubmit={search}>
        <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="name">
            Name:
          </label>
          <div className="col-sm-10">
            <input
              type="text"
              className="form-control"
              id="name"
              name="name"
          
              onChange={(e)=>{let value=e.target.value;
                    setName(value);}}
              value={name} 
            />
          </div>
        </div>
        <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="keywords">
            Keywords:
          </label>
          <div className="col-sm-10">
            <textarea
              className="form-control"
              id="keyword"
              name="keyword"
            
              onChange={(e)=>{let value=e.target.value;
                    setKeywords(value);}}
              value={keywords} 
            />
          </div>
        </div>

        <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="DateFrom">
            DateFrom:
          </label>
          <div className="col-sm-10">
            <input
              type="date"
              className="form-control"
              id="DateFrom"
              name="DateFrom"
          
              onChange={(e)=>{let dateFrom=e.target.value;
                    setDateFrom(dateFrom);}}
              value={dateFrom} 
            />
          </div>

          <div className="form-group row">
          <label className="col-sm-2 col-form-label" htmlFor="dateTo">
          dateTo:
          </label>
          <div className="col-sm-10">
            <input
              type="date"
              className="form-control"
              id="dateTo"
              name="dateTo"
          
              onChange={(e)=>{let value=e.target.value;
                    setDateTo(value);}}
              value={dateTo} 
            />
          </div>
        </div>
        </div>
        <input type="submit" className="btn btn-success" value="Search" />
      </form>
      <button onClick={() => { changeAvailability()}}>
        
        new Analysis 
      </button>
    </div>
  );
}

export default AnalysisMenu;
