import React, { useEffect, useState } from "react";
import ReactDOM from 'react-dom/client';
import Image from "../Models/Image";

function ImageSlider(props){
    let [images,setImages]=useState(props.images);
    let [counter,setCounter]=useState(0);
    useEffect(() => {
      
    }, []);
    let deleteImage = async (number) => {
     
        let newImages=images;
        newImages.splice(number,1);
        
        setImages(newImages);
        props.save(newImages);
      };
    
      let saveImage = (counter,image,isNew) => {
          if(image.base64!==""){
            if(isNew){
            setImages([...images, image]);
          }
          else{
            let newImages=images;
            newImages[counter]=image;
            setImages(newImages);

          }
          props.save(images);}
      };
      let step=(value)=>{
          let length=images.length;
          let target=counter+value;
          if(target>=0 && target<length){
          setCounter(()=>{let target=counter+value; console.log(target); return target;});
          }
        }

return(<div>
    <div className="imageSlider">
    <button onClick={()=>{step(-1)}}>left</button>
    <Image name={images[counter].name} description={images[counter].description} image={images[counter].base64} id={counter} delete={(number)=>{deleteImage(number)}} save={(counter,image,isNew)=>{saveImage(counter,image,isNew)}}/>
    <button onClick={()=>{step(1)}}>right</button>
    </div>
    <div className="imageSlider">
    {images.map((image)=>{
        return(<img src={image.base64} alt={image.name} width="100px" height="100px"/>);
    })}
    </div>
</div>);
      
}
export default ImageSlider;
